import React from 'react'
import ReactDOM from 'react-dom'


const Header = ({course}) => {
  return (
    <h1>{course.name}</h1>
  )
}

const Part = ({name, exercises}) => {
  return (
    <p>
      {name} {exercises}
    </p>
  )
}

const Content = (props) => {
  const { notes } = props
  const rows = () =>
    notes.map(note => <li key={note.id}> {note.parts.name} {note.parts.exercises} </li>)
  return (
    <div>
      {rows()}
    </div>
  )
}

