import React from 'react'
import ReactDOM from 'react-dom'


const Header = ({course}) => {
  console.log(course)
  return (
    <h1>{course.name}</h1>
  )
}

const Part = ({name, exercises}) => {
  return (
    <p>
      {name} {exercises}
    </p>
  )
}

const Content= ({parts}) =>{
    return(
      <div>
    {parts.map(part =>
    <Part key={part.id}
    name={part.name}
    exercises={part.exercises}
    />)}
      </div>
    )
}

const Total = ({course}) => {
 const exercises = course.parts.map(part => part.exercises)
 const value = (exercises.reduce((s, p) => s + p))
   return (
    <p>yhteensä {value} </p>
  )
}

const Course = ({courses}) => {
  return (
    courses.map(part => {
      return(
          <div>
            <Header course={part} />
            <Content course={part.parts} />
            <Total course={part.parts}/>
          </div>
        )
    })

  )
}

const App = () => {
  const courses = [
    {
      name: 'Half Stack -sovelluskehitys',
      id: 1,
      parts: [
        {
          name: 'Reactin perusteet',
          exercises: 10,
          id: 1
        },
        {
          name: 'Tiedonvälitys propseilla',
          exercises: 7,
          id: 2
        },
        {
          name: 'Komponenttien tila',
          exercises: 14,
          id: 3
        }
      ]
    },
    {
      name: 'Node.js',
      id: 2,
      parts: [
        {
          name: 'Routing',
          exercises: 3,
          id: 1
        },
        {
          name: 'Middlewaret',
          exercises: 7,
          id: 2
        }
      ]
    }
  ]

  return (
    <div>
      <Course courses={courses} />
    </div>
  )
}

ReactDOM.render(<App />, document.getElementById('root'))
